import React, {ChangeEvent, useState} from 'react';


type EditableSpanType = {
    title:string
    ChangeTask:(value:string)=>void
}


export const EditableSpan = (props:EditableSpanType) => {

    const [edit,setEdit]=useState(false)

    const onClickSpan = () => {
        setEdit(!edit)
    }

    const ChangeTask = (e:ChangeEvent<HTMLInputElement>)=> {
        props.ChangeTask(e.currentTarget.value)
    }
    return (

        edit ?
            <input autoFocus onChange={ChangeTask} onBlur={onClickSpan} value={props.title}/>
            :
            <span onDoubleClick={onClickSpan}>{props.title}</span>

    );
};
