import React, {ChangeEvent, useState} from 'react';
import {FilterValuesType} from "./App";

type TaskTypeArray = {
    id:string;
    title:string;
    isDone:boolean
}

type TaskType = {
    title:string
    task:TaskTypeArray[]
    FilterTask:(nameButton:FilterValuesType)=>void
    removeTask:(taskId:string)=>void
    changeStatus:(id:string,e:boolean)=>void
    addMessage:(valueInput:string)=>void
}



export  const Todolist:React.FC<TaskType> = ({title,task,
                                                 FilterTask,removeTask,changeStatus,addMessage,...props}) => {

const onClickFilter= (nameButton:FilterValuesType)=>{
    FilterTask(nameButton)
}

const DeleteTask = (taskId:string)=> {
    removeTask(taskId)
}

      const ChangeStatus = (id:string,e:boolean)=> {
    changeStatus(id,e)
      }

      let [valueInput,setValueInput]=useState('')


      const onChangeValueInput = (e:ChangeEvent<HTMLInputElement>)=>{
    setValueInput(e.currentTarget.value)
      }

      const AddMessage = ()=> {
    addMessage(valueInput)
      }

    return (
        <div>
            <h3>{title}</h3>
            <input onChange={onChangeValueInput}/>
            <button onClick={AddMessage}>Add</button>
          <ul>
              {task.map((el)=>{
                  return (
                      <li key={el.id}>
                          {el.title}
                          <input type={'checkbox'} onChange={(e:ChangeEvent<HTMLInputElement>)=>ChangeStatus(el.id,e.currentTarget.checked)} checked={el.isDone}/>
                          <button onClick={()=>DeleteTask(el.id)}>X</button>
                      </li>
                  )
              })}
          </ul>
            <button onClick={()=>onClickFilter('all')}>All</button>
            <button onClick={()=>onClickFilter('active')}>Active</button>
            <button onClick={()=>onClickFilter('completed')}>Completed</button>
        </div>
    );
};
