import React, {useState} from 'react';


type PropsType = {
    title: string
    tasks: Array<TaskType>
    callback:(id:number)=>void;


}

type TaskType = {
    id: number
    title: string
    isDone: boolean
}

export function Todolist(props: PropsType) {


    const [nameButton,setButton]=useState('All')

    let filtred=props.tasks
    if(nameButton==='Active'){  filtred=props.tasks.filter((el)=>el.isDone===true)}
    if(nameButton==='Completed'){  filtred=props.tasks.filter((el)=>el.isDone===false)}

    const callBackButton = (nameButton:string)=> {
        setButton(nameButton)
    }



    return <div>
        <h3>{props.title}</h3>
        <div>
            <input/>
            <button>+</button>
        </div>

        <ul> {filtred.map((el) => {
            return (
                <div key={el.id}>
                    <li>
                        <input type="checkbox" checked={el.isDone}/>
                        <span>{el.title}</span>
                        <button onClick={(callback) => props.callback(el.id)}>X</button>
                    </li>

                </div>
            )
        })


        }</ul>
        <div>
            <button onClick={()=>callBackButton('All')}>All</button>
            <button onClick={()=>callBackButton('Active')}>Active</button>
            <button onClick={()=>callBackButton('Completed')}>Completed</button>
        </div>
    </div>
}
