import React, {ChangeEvent,KeyboardEvent, useState} from 'react';
import {FilterValuesType} from './App';
import  s from './Todolist.module.css'
import {Button} from "./Button";

type TodolistType = {
   title:string
    task:ArrayTypeTask[]
    filtered:(nameButton:FilterValuesType)=>void
    removeTask:(id:string)=>void
    addMessage:(inputValue:string)=>void
    ChangeInputCheck:(event:boolean,id:string)=>void

}

type ArrayTypeTask = {
    id:string;
    title:string;
    isDone:boolean;
}


export const Todolist = (props:TodolistType) => {


    const onclickHandlerFilter = (nameButton: FilterValuesType) => {
        props.filtered(nameButton)
    }

    const DeleteTask = (id: string) => {
        props.removeTask(id)
    }

    let [inputValue, setInputValue] = useState('')

    const onInputChange = (event: ChangeEvent<HTMLInputElement>) => {
        setInputValue(event.currentTarget.value)
    }

    const AddMessage = () => {
        if(inputValue.trim() !==''){ props.addMessage(inputValue.trim())}
        else {setError('Tittle is reqied')}
       setInputValue('')

    }

    let [error,setError]=useState<string|null>(null)



    const onKeyDown = (event:KeyboardEvent<HTMLInputElement>)=> {
        setError(null)
        if(event.key==='Enter'){AddMessage()}
    }

    const ChangeInputCheck = (event:boolean,id:string)=> {
        props.ChangeInputCheck(event,id)
    }
        return (
            <div>
                <h3>{props.title}</h3>
               <input className={error ? s.error:''} onKeyDown={onKeyDown} value={inputValue} onChange={onInputChange}/>
                <button onClick={AddMessage}>Add</button>
                {error && <div className={s.errorMessage}>{error}</div>}
                <ul>
                    {props.task.map((el) => {
                        return (
                            <li key={el.id}>
                                {el.title}
                                <input onClick={(event)=>ChangeInputCheck(event.currentTarget.checked,el.id)} type={'checkbox'} checked={el.isDone}/>
                                <button onClick={() => DeleteTask(el.id)}>X</button>

                            </li>
                        )
                    })}
                </ul>
                <Button name={'all'} started={()=>onclickHandlerFilter('all')}  filtered={props.filtered}/>
                <Button name={'active'} started={()=>onclickHandlerFilter('active')} filtered={props.filtered}/>
                <Button name={'completed'} started={()=>onclickHandlerFilter('completed')} filtered={props.filtered}/>
                {/*<button onClick={() => onclickHandlerFilter('all')}>All</button>*/}
                {/*<button onClick={() => onclickHandlerFilter('active')}>Active</button>*/}
                {/*<button onClick={() => onclickHandlerFilter('completed')}>Completed</button>*/}

            </div>
        );
    };
