import React, {useState} from 'react';


type EnableSpanType = {
    title:string
}


export const EnableSpan:React.FC<EnableSpanType> = ({title,...props}) => {

    const [editMode,SetEditMode]=useState(false)

const ChangeState = ()=> {
        SetEditMode(!editMode)
}


  return (
      <div>
          editMode     <span onClick={ChangeState}>

        {title}
</span>? : <input/>
      </div>
  )
};
