import React, {ChangeEvent, useState} from 'react';


type EditableSpan = {
    title:string
    ChangeSpan:()=>void
}

export const EditableSpan:React.FC<EditableSpan> = ({title,ChangeSpan,...props}) => {


    let [edit,SetEdit]=useState(true)

    const changeSpan = (e:ChangeEvent<HTMLInputElement>)=> {
      SetEdit(!edit)
       ChangeSpan(e.currentTarget.value)
    }

    return (


   edit ?  <span onDoubleClick={(m)=>changeSpan}>
            {title}</span> :    <input  value={title} autoFocus onChange={(e:ChangeEvent<HTMLInputElement>)=>changeSpan(e.currentTarget.value)}/>



    );
};
