import React, {ChangeEvent,KeyboardEvent, useState} from 'react';
import {FilterValuesType} from "./App";
import s from './Todolist.module.css'

type TaskTypeArray = {
    id:string;
    title:string;
    isDone:boolean
}

type TaskType = {
    title:string
    task:TaskTypeArray[]
    FilterTask:(nameButton:FilterValuesType)=>void
    removeTask:(taskId:string)=>void
    changeStatus:(id:string,e:boolean)=>void
    addMessage:(valueInput:string)=>void
    filter:FilterValuesType
}



export  const Todolist:React.FC<TaskType> = ({title,task,
                                                 FilterTask,removeTask,changeStatus,addMessage,filter,...props}) => {

const onClickFilter= (nameButton:FilterValuesType)=>{
    FilterTask(nameButton)
}

const DeleteTask = (taskId:string)=> {
    removeTask(taskId)
}

      const ChangeStatus = (id:string,e:boolean)=> {
    changeStatus(id,e)
      }

      let [valueInput,setValueInput]=useState('')


      const onChangeValueInput = (e:ChangeEvent<HTMLInputElement>)=>{
    setValueInput(e.currentTarget.value)
      }

      let [error,SetError]=useState<string|null>('')


      const AddMessage = ()=> {
    if(valueInput.trim()!==''){  addMessage(valueInput.trim())}
    else {SetError('Title is reqiuaet')}
          setValueInput('')
      }

      const onKeyDown = (e:KeyboardEvent<HTMLInputElement>)=> {
         if(e.key==='Enter'){AddMessage ()}


      }
    return (
        <div>
            <h3>{title}</h3>
            <input onKeyDown={onKeyDown} className={error? s.errorMessage :''} value={valueInput} onChange={onChangeValueInput}/>
            <button  onClick={AddMessage}>Add</button>
            {error && <div className={error? s.error :''}>{error}</div>}
          <ul>
              {task.map((el)=>{
                  return (
                      <li key={el.id}>
                          {el.title}
                          <input type={'checkbox'} onChange={(e:ChangeEvent<HTMLInputElement>)=>ChangeStatus(el.id,e.currentTarget.checked)} checked={el.isDone}/>
                          <button onClick={()=>DeleteTask(el.id)}>X</button>
                      </li>
                  )
              })}
          </ul>
            <button className={filter==='all'? s.active_Button:''} onClick={()=>onClickFilter('all')}>All</button>
            <button onClick={()=>onClickFilter('active')}>Active</button>
            <button onClick={()=>onClickFilter('completed')}>Completed</button>
        </div>
    );
};
