import React, {ChangeEvent, useState} from 'react';
import {FilterValuesType} from './App';

type TodolistType = {
   title:string
    task:ArrayTypeTask[]
    filtered:(nameButton:FilterValuesType)=>void
    removeTask:(id:string)=>void
    addMessage:(inputValue:string)=>void
}

type ArrayTypeTask = {
    id:string;
    title:string;
    isDone:boolean;
}


export const Todolist = (props:TodolistType) => {


    const onclickHandlerFilter = (nameButton: FilterValuesType) => {
        props.filtered(nameButton)
    }

    const DeleteTask = (id: string) => {
        props.removeTask(id)
    }

    let [inputValue, setInputValue] = useState('')

    const onInputChange = (event: ChangeEvent<HTMLInputElement>) => {
        setInputValue(event.currentTarget.value)
    }

    const AddMessage = () => {
        props.addMessage(inputValue)
        setInputValue('')
    }

        return (
            <div>
                <h3>{props.title}</h3>
                <input value={inputValue} onChange={onInputChange}/>
                <button onClick={AddMessage}>Add</button>
                <ul>
                    {props.task.map((el) => {
                        return (
                            <li key={el.id}>
                                {el.title}
                                <input type={'checkbox'} checked={el.isDone}/>
                                <button onClick={() => DeleteTask(el.id)}>X</button>
                            </li>
                        )
                    })}
                </ul>
                <button onClick={() => onclickHandlerFilter('all')}>All</button>
                <button onClick={() => onclickHandlerFilter('active')}>Active</button>
                <button onClick={() => onclickHandlerFilter('completed')}>Completed</button>
            </div>
        );
    };
