import React, {ChangeEvent, useState} from 'react';


type EnableSpanType = {
    title:string
    titleSpan:string
}


export const EnableSpan:React.FC<EnableSpanType> = ({title,titleSpan,...props}) => {

    const [editMode,SetEditMode]=useState(false)

const ChangeState = ()=> {
        SetEditMode(!editMode)
}

    const changeSpanTitle = (e:ChangeEvent<HTMLInputElement>)=> {
    titleSpan(e.currentTarget.value)
     }

  return (

          editMode
          ? <input onChange={changeSpanTitle} autoFocus onBlur={ChangeState} value={title}/>
          : <span onDoubleClick={ChangeState}>{title}</span>

  )
};
