import React from 'react';
import {ButtonFilterType} from "./App";

type  TodolistType = {
    name:string
    filter:(nameButton:ButtonFilterType)=>void
    task:TaskType[]
}

type TaskType = {
       id:string,
       title:string,
       isDone:boolean
}

export const Todolist:React.FC<TodolistType> = ({
                                                    name,
                                                   filter,
                                                    task,
                                                    ...props
                                                }) => {
    const onClickHandler = (nameButton:ButtonFilterType)=> {
        filter(nameButton)
    }
    return (
        <div>
            <h3>{name}</h3>
            <input/>
            <button>Add</button>
            <ul>{task.map((el)=>{
                return ( <li key={el.id}>
                    {el.title}
                    <input type={"checkbox"} checked={el.isDone} />
                    <button>X</button>
                </li>)
            })}

            </ul>
               <button onClick={()=>onClickHandler('All')}>All</button>
               <button onClick={()=>onClickHandler('Active')}>Active</button>
               <button onClick={()=>onClickHandler('Completed')}>Completed</button>

        </div>
    );
};
