import React, {ChangeEvent,KeyboardEvent, useState} from 'react';
import {FilterValuesType} from "./App";
import s from  './Todolist.module.css'
import {Button} from "./Button";

type TodolistType = {
    title:string;
    task:Array<ArrayTodolistType>
    filter:(nameButton:FilterValuesType)=>void
    deleteTask:(id:string)=>void
    addMessage:(inputValue:string)=>void
    InputCheck:(event:boolean,id:string)=>void
    Filter:FilterValuesType
    todolistID:string
}

type ArrayTodolistType = {
    id:string;
    title:string;
    isDone:boolean;
}




export const Todolist = (props:TodolistType) => {

    const onclickHandler =(nameButton:FilterValuesType)=> {
        props.filter(nameButton)
    }

    const onclickHandlerDelete =(id:string)=> {
       props.deleteTask(id)
    }

    let [inputValue,setInputValue]=useState('')


    const onChangeHandler = (event:ChangeEvent<HTMLInputElement>)=> {
       setInputValue(event.currentTarget.value)
    }
    const addMessage = ()=>{
        if (inputValue.trim()!==''){props.addMessage(inputValue.trim())}
        else {setError('Title is requit')}
        setInputValue('')

    }

    let [error,setError]=useState('')

    const onKeyDown = (event:KeyboardEvent<HTMLInputElement>)=> {
     if (event.key==='Enter'){addMessage()}
    }

    const onChangeCheck = (event:boolean,id:string)=> {
        props.InputCheck(event,id)
    }
    return (
        <div>

     <h3>{props.title}</h3>
            <input value={inputValue} onChange={onChangeHandler} onKeyDown={onKeyDown}/>
            <button onClick={addMessage}>Add</button>
            {error && <div className={s.messageError}>{error}</div>}
            <ul>
                {props.task.map((el)=>{
                    return (
                        <li className={el.isDone===true ? s.completed:''} key={el.id}  >
                            {el.title}
                            <input type={"checkbox"} className={error ? s.error:''} onChange={(event)=>onChangeCheck(event.currentTarget.checked,el.id)}  checked={el.isDone}/>

                            <button onClick={()=>onclickHandlerDelete(el.id)}>X</button>
                        </li>

                    )
                })}
            </ul>

            {/*<button onClick={()=>onclickHandler('all')}>All</button>*/}
            {/*<button onClick={()=>onclickHandler('active')}>Active</button>*/}
            {/*<button onClick={()=>onclickHandler('completed')}>Completed</button>*/}
            <Button name={'all'} Filter={props.Filter} started={()=>onclickHandler('all')}/>
            <Button name={'active'} Filter={props.Filter}started={()=>onclickHandler('active')}/>
            <Button name={'completed'} Filter={props.Filter} started={()=>onclickHandler('completed')}/>
        </div>
    );
};
