import React, {ChangeEvent,KeyboardEvent, useState} from 'react';
import {FilterValueType} from "./App";
import s from  "./Todolist.module.css"

type TodolistType = {
    todolistID:string
    title:string;
    task:TaskType[]
    removeTask:(taskId:string)=>void
    filteredTask:(todolistID:string,nameButton:FilterValueType)=>void
    AddMessage:(value:string)=>void
    changeStatus:(id:string,e:boolean)=>void
    Filter:FilterValueType
}

type TaskType= {
    id:string;
    title:string;
    isDone:boolean
}






export  const Todolist:React.FC<TodolistType> = ({ todolistID,task,
                                                     title,
                                                     removeTask,filteredTask,
                                                     AddMessage, changeStatus,
                                                     Filter,...props}) => {


    const onClickHandlerRemoveTask = (taskId:string)=> {
        removeTask(taskId)
    }

    const onclickFilter = (todolistID:string,nameButton:FilterValueType)=> {
       filteredTask(todolistID,nameButton)
  }

  const [value,SetValue]=useState('')

  const onChangeInput = (e:ChangeEvent<HTMLInputElement>)=> {
        SetValue(e.currentTarget.value)
        SetError('')
  }

  const [error,SetError]=useState('')

  const onClickHandlerAddMessage = ()=> {
        if(value.trim()!==''){AddMessage(value.trim()) }
        else (SetError('Title is requiet'))
      SetValue('')



  }


  const onKeyDown = (event:KeyboardEvent<HTMLInputElement>)=> {
        if(event.key==='Enter'){onClickHandlerAddMessage()}

  }

     const onclickHandlerStatusInput = (id:string,e:boolean)=> {
       changeStatus(id,e)
}
    return (
        <div>
            <h3>{title}</h3>
            <input className={error ? s.error_message : ''} onChange={onChangeInput} value={value} onKeyDown={onKeyDown}/>
            <button onClick={onClickHandlerAddMessage}>Add</button>
            {error && <div  className={s.error}>{error}</div>}
           <ul>{task.map((el)=>{
               return( <li key={el.id}  className={el.isDone ? s.Completed : ''}>
                   {el.title}
                   <input  onChange={(e)=>onclickHandlerStatusInput(el.id,e.currentTarget.checked)} type={"checkbox"} checked={el.isDone}/>
                   <button onClick={()=>onClickHandlerRemoveTask(el.id)}>X</button>
               </li>)
               })

           }

           </ul>

            <button  className={Filter==='All' ? s.active: ''} onClick={()=>onclickFilter('All')}>All</button>
            <button className={Filter==='Active' ? s.active: ''} onClick={()=>onclickFilter('Active')}>Active</button>
            <button className={Filter==='Completed' ? s.active: ''} onClick={()=>onclickFilter('Completed')}>Completed</button>
        </div>
    );
};
