    //         {id: v1(), title: "JS", isDone: true}
    //     ],
    //     [todolistId2]: [
    //         {id: v1(), title: "Milk", isDone: true},
    //         {id: v1(), title: "React Book", isDone: true}
    //     ]
    // });
const  deleteTask = (id:string)=> {

   let  newTask=tasks.filter((el)=>el.id !==id)
    setTasks(newTask)
    return newTask
}

// let [filtered,setFiltered]=useState<FilterValuesType>('all')

const filter = (todolistID:string,nameButton:FilterValuesType)=> {

setTodolist(todolist.map((el)=>el.id===todolistID ? {...el,filter:nameButton}:el))
}

const addMessage = (inputValue:string)=> {
    let newTask = { id: v1(), title: inputValue, isDone: true }
    setTasks([newTask,...tasks])
 }

// const FilteredButton = ()=> {
//     let newTask =tasks
//     if(filtered==='active'){newTask=tasks.filter((el)=>el.isDone===false)}
//     if(filtered==='completed'){newTask=tasks.filter((el)=>el.isDone===true)}
//     return newTask
// }

const InputCheck = (event:boolean,id:string)=> {
    return setTasks(tasks.map((el)=>el.id===id ? {...el,isDone:event}:el))

}

    return (
        <div className="App">
            {todolist.map((el)=>{
                const FilteredButton = ()=> {
                    let newTask =tasks
                    if(el.filter==='active'){newTask=tasks.filter((el)=>el.isDone===false)}
                    if(el.filter==='completed'){newTask=tasks.filter((el)=>el.isDone===true)}
                    return newTask
                }
                return(
                    <Todolist task={FilteredButton()}  title={el.title}
                              key={el.id}
                              todolistID={el.id}
                              filter={filter}
                              deleteTask={deleteTask}
                              addMessage={addMessage}
                              InputCheck={InputCheck}
                              Filter={el.filter}/>
                )
            })}


        </div>
    );
}

export default App;





























