import React, {ChangeEvent, useState} from 'react';
import {FilterValueType} from "./App";


type TodolistType = {
    title:string;
    task:TaskType[]
    removeTask:(taskId:string)=>void
    filteredTask:(nameButton:FilterValueType)=>void
    AddMessage:(value:string)=>void
}

type TaskType= {
    id:string;
    title:string;
    isDone:boolean
}






export  const Todolist:React.FC<TodolistType> = ({task,title,
                                                     removeTask,filteredTask,
                                                     AddMessage,...props}) => {


    const onClickHandlerRemoveTask = (taskId:string)=> {
        removeTask(taskId)
    }

    const onclickFilter = (nameButton:FilterValueType)=> {
       filteredTask(nameButton)
  }

  const [value,SetValue]=useState('')

  const onChangeInput = (e:ChangeEvent<HTMLInputElement>)=> {
        SetValue(e.currentTarget.value)
  }

  const onClickHandlerAddMessage = ()=> {
        AddMessage(value)
  }

    return (
        <div>
            <h3>{title}</h3>
            <input onChange={onChangeInput}/>
            <button onClick={onClickHandlerAddMessage}>Add</button>
           <ul>{task.map((el)=>{
               return( <li key={el.id}>
                   {el.title}
                   <input type={"checkbox"} checked={el.isDone}/>
                   <button onClick={()=>onClickHandlerRemoveTask(el.id)}>X</button>
               </li>)
               })

           }

           </ul>

            <button onClick={()=>onclickFilter('All')}>All</button>
            <button onClick={()=>onclickFilter('Active')}>Active</button>
            <button onClick={()=>onclickFilter('Completed')}>Completed</button>
        </div>
    );
};
