import React,{useState} from 'react';
import './App.css';
import {v1} from 'uuid'
import {TaskType, Todolist} from "./Todolist";


type TodolistType= {
    id:string;
    title:string;


}

type TaskStateType = {
    [key:string]:DataType
}

type DataType = {
    data:TaskType[]
    filter:ButtonType
}


export type ButtonType ='All'|'Active'|'Completed'

let todolistId1 = v1();
let todolistId2 = v1();


function App() {

    let [tasks, setTasks] = useState<TaskStateType>({
        [todolistId1] :{
            data:[
            {id: v1(), title: "HTML&CSS", isDone: true},
            {id: v1(), title: "JS", isDone: true},
            {id: v1(), title: "ReactJS", isDone: false},
            {id: v1(), title: "Rest API", isDone: false},
            {id: v1(), title: "GraphQL", isDone: false},
        ],
        filter:'All'},
        [todolistId2]:{
    data:[
            {id: v1(), title: "HTML&CSS", isDone: true},
            {id: v1(), title: "JS", isDone: true},
            {id: v1(), title: "ReactJS", isDone: false},
            {id: v1(), title: "Rest API", isDone: false},
            {id: v1(), title: "GraphQL", isDone: false},
        ],
        filter:'Completed'}
        }
    );


let [todolists, setTodolists] = useState<TodolistType[]>([
    {id: todolistId1, title: "What to learn"},
    {id: todolistId2, title: "What to buy"}
])

    const removeTodolist = (todolistId:string)=> {
    setTodolists(todolists.filter((el)=>el.id!==todolistId))
        delete tasks[todolistId]

    }
    const removeTask = (todolistId:string,id:string)=> {
        //let newTask=tasks.filter((el)=>el.id!=id)
        //setTasks(newTask)
        setTasks({...tasks,[todolistId]:tasks[todolistId].data.filter((el)=>el.id!==id)})
    }

    // let [nameButton,setNameButton]=useState<ButtonType>('All')

    const ChangeFilter = (todolistId:string,nameButton:ButtonType)=> {
        setTodolists(todolists.map((el)=>el.id===todolistId ? {...el,filter:nameButton} :el))
    }


    const AddMessage = (todolistId:string,inputValue:string)=> {
        // let newTask={id: v1(), title: inputValue, isDone: true}
        // setTasks({...tasks,[todolistId]:[newTask,...tasks[todolistId]]})
        //setTasks([newTask,...tasks])
    }
    const InputCheck = (todolistId:string,event:boolean,id:string)=> {
        //setTasks(tasks.map((el)=>el.id=== id ? {...el,isDone:event}:el))
// setTasks({...tasks,[todolistId]:tasks[todolistId].map((el)=>el.id===id ? {...el,isDone:event} : el)})
    }


    return (
        <div className="App">
            {todolists.map((el)=>{
                const Filtered = ()=> {
                    let filteredTask=tasks[el.id].data
                    if(tasks[el.id].filter==='Active'){filteredTask=tasks[el.id].data.filter((el)=>el.isDone===false)}
                    if(tasks[el.id].filter==='Completed'){filteredTask=tasks[el.id].data.filter((el)=>el.isDone===true)}
                    return filteredTask
                }

                return(
                    <Todolist
                              key={el.id}
                              todolistId={el.id}
                              task={Filtered()}
                              name={el.title}
                              removeTask={removeTask}
                              ChangeFilter={ChangeFilter}
                              addMessage={AddMessage}
                              InputCheck={InputCheck}
                              Filter={tasks[el.id].filter}
                              removeTodolist={removeTodolist}/>
                )
            })}

        </div>
    );
}

export default App;



























