import React, {useState} from 'react';
import './App.css';
import {Todolist} from './Todolist';
import { v1 } from 'uuid';




export type FilterValuesType = "all" | "active" | "completed";

function App() {

    let [tasks, setTasks] = useState([
        { id: v1(), title: "HTML&CSS", isDone: true },
        { id: v1(), title: "JS", isDone: true },
        { id: v1(), title: "ReactJS", isDone: false },
        { id: v1(), title: "Rest API", isDone: false },
        { id: v1(), title: "GraphQL", isDone: false },
    ]);

const  addMessage = (inputValue:string)=>{
    let addTask = { id: v1(), title: inputValue, isDone: true }
    return setTasks([addTask,...tasks])
}

    let [filter,setFilter]=useState('all')

const filtered = (nameButton:FilterValuesType)=> {
    setFilter(nameButton)
}



const removeTask = (id:string)=> {
     let newTask=tasks.filter((el)=>el.id !==id)
    return setTasks(newTask)
}

const TaskFiltered = ()=> {
        let newFilteredTask =tasks
        if(filter==='active'){ newFilteredTask=tasks.filter((el)=>el.isDone===false)}
    if(filter==='completed'){ newFilteredTask=tasks.filter((el)=>el.isDone===true)}
    return newFilteredTask
}



const ChangeInputCheck = (event:boolean,id:string)=> {

    let newTask =tasks
    setTasks(newTask=tasks.map((el)=>el.id=== id ? {...el,isDone: event}  : el))
    return newTask
}
    return (
        <div className="App">
          <Todolist title={'What to learn'} task={TaskFiltered()}
                    filtered={filtered}
                    removeTask={removeTask}
                    addMessage={addMessage}
                    ChangeInputCheck={ChangeInputCheck}
          filter={filter}/>
        </div>
    );
}

export default App;





























