import React, {useState} from 'react';
import './App.css';
import {Todolist} from './Todolist';
import { v1 } from 'uuid';




export type FilterValuesType = "all" | "active" | "completed";

function App() {

    let [tasks, setTasks] = useState([
        { id: v1(), title: "HTML&CSS", isDone: true },
        { id: v1(), title: "JS", isDone: true },
        { id: v1(), title: "ReactJS", isDone: false },
        { id: v1(), title: "Rest API", isDone: false },
        { id: v1(), title: "GraphQL", isDone: false },
    ]);

const  deleteTask = (id:string)=> {

   let  newTask=tasks.filter((el)=>el.id !==id)
    setTasks(newTask)
    return newTask
}

let [filtered,setFiltered]=useState('all')

const filter = (nameButton:FilterValuesType)=> {
setFiltered(nameButton)
}

const addMessage = (inputValue:string)=> {
    let newTask = { id: v1(), title: inputValue, isDone: true }
    setTasks([newTask,...tasks])
}
const FilteredButton = ()=> {
    let newTask =tasks
    if(filtered==='active'){newTask=tasks.filter((el)=>el.isDone===false)}
    if(filtered==='completed'){newTask=tasks.filter((el)=>el.isDone===true)}
    return newTask
}

const InputCheck = ()=> {
    
}

    return (
        <div className="App">
          <Todolist task={FilteredButton()}  title={'What to learn'}
                    filtered={filter}
                    deleteTask={deleteTask}
                    addMessage={addMessage}
                    InputCheck={InputCheck}/>

        </div>
    );
}

export default App;





























