import React, {ChangeEvent, useState} from 'react';
import {ButtonFilterType} from "./App";

type  TodolistType = {
    name:string
    FilterNameButton:(nameButton:ButtonFilterType)=>void
    removeTask:(id:string)=>void
    AddMessage:(valueInput:string)=>void
    task:TaskType[]
}

type TaskType = {
       id:string,
       title:string,
       isDone:boolean
}

export const Todolist:React.FC<TodolistType> = ({
                                                    name,
                                                    FilterNameButton,
                                                    removeTask,
                                                    AddMessage,
                                                    task,
                                                    ...props
                                                }) => {
    const onClickHandler = (nameButton:ButtonFilterType)=> {
        FilterNameButton(nameButton)
    }

    const  onClickHandlerDeleteTask= (id:string)=> {
        removeTask(id)
    }

    let [valueInput,setValueInput]=useState('')

    const onChangeInputHandler = (event:ChangeEvent<HTMLInputElement>)=> {
     setValueInput(event.currentTarget.value)
    }

    const onclickHandlerAddMessage = ()=> {
        AddMessage(valueInput)
        setValueInput('')
    }
    return (
        <div>
            <h3>{name}</h3>
            <input onChange={onChangeInputHandler}/>
            <button onClick={onclickHandlerAddMessage}>Add</button>
            <ul>{task.map((el)=>{
                return ( <li key={el.id}>
                    {el.title}
                    <input type={"checkbox"} checked={el.isDone} value={valueInput} />
                    <button onClick={()=>onClickHandlerDeleteTask(el.id)}>X</button>
                </li>)
            })}

            </ul>
               <button onClick={()=>onClickHandler('All')}>All</button>
               <button onClick={()=>onClickHandler('Active')}>Active</button>
               <button onClick={()=>onClickHandler('Completed')}>Completed</button>

        </div>
    );
};
