import React, {useState} from 'react';
import './App.css';
import {Todolist} from "./Todolist";
import {v1} from 'uuid'

export type FilterValuesType = "all" | "active" | "completed";



function App() {


    let [ tasks, setTask] =useState([
        { id: v1(), title: "HTML&CSS", isDone: true },
        { id: v1(), title: "JS", isDone: true },
        { id: v1(), title: "ReactJS", isDone: false },
        {id: v1(), title: "Rest API", isDone: false},
        {id: v1(), title: "GraphQL", isDone: false}
    ])

   let[filter,setFilter] =useState<FilterValuesType>('all')


      const FilterTask = (nameButton:FilterValuesType)=> {
        setFilter(nameButton)
       }

       const FilteredTask = ()=> {
        let newArrayTask=tasks
        if (filter==='active'){newArrayTask=tasks.filter((el)=>el.isDone===false)}
        if (filter==='completed'){newArrayTask=tasks.filter((el)=>el.isDone===true)}
        return newArrayTask
       }

       const removeTask=(taskId:string)=>{

        setTask(tasks.filter((el)=>el.id!==taskId))
       }

       const  changeStatus=(id:string,isDone:boolean)=>{
      setTask(tasks.map((el)=>el.id===id ? {...el,isDone: isDone}: el))
       }

       const addMessage=(valueInput:string)=>{
        let newTask= { id: v1(), title: valueInput, isDone: true }
           setTask([...tasks,newTask])
       }

    return (
        <div className="App">
       <Todolist
           title={'What to learn'}
           task={FilteredTask()}
           FilterTask={FilterTask}
           removeTask={removeTask}
           changeStatus={changeStatus}
           addMessage={addMessage}
       />

        </div>
    );
}

export default App;
