import React, {useState} from 'react';


type EditableSpan = {
    title:string
}

export const EditableSpan:React.FC<EditableSpan> = ({title,...props}) => {


    let [edit,SetEdit]=useState(false)

    const changeSpan = ()=> {
      SetEdit(!!edit)
    }

    return (
        <div>{edit?      <span onDoubleClick={changeSpan}>
            {title}
        </span>:<input onChange={changeSpan}/>}


        </div>




    );
};
